mapper.update:
update jiandan
set fullPath = #{fullpath,jdbcType=VARCHAR},
  updateTime = #{updatetime,jdbcType=TIMESTAMP},
  ext = #{ext,jdbcType=INTEGER}
where md5 = #{md5,jdbcType=VARCHAR}
  and name = #{name,jdbcType=VARCHAR}

mapper.select:
select md5, name, fullPath, updateTime, ext
from jiandan
where md5 = #{md5,jdbcType=VARCHAR}
  and name = #{name,jdbcType=VARCHAR}
